'--',
'-- Do your processing here when the sequence changes or alternatively',
'-- let the user decide when to save the changes and move this action to ',
'-- a click event on a button e.g. "Save Changes"',
'--',
'',
'declare ',
'    l_bef_sq        flw_type.priority%type;',
'    l_before        flw_type.id%type;',
'    l_after         flw_type.id%type;',
'    l_vc_arr_a_sq   apex_t_varchar2;',
'    l_ids           varchar2(4000);',
'    l_sqs           varchar2(4000);',
'    l_count         number;',
'begin',
'    select json_value(:P2_CLASSIC_RPT_SORT_INFO, ''$.sequence.before.size()'') as result ',
'    into l_count',
'    from dual;',
'    ',
'    for i in 0 .. l_count-1',
'    loop',
'        ',
'        l_before := to_number(json_value(:P2_CLASSIC_RPT_SORT_INFO, ''$.sequence.before[''||i||'']''));',
'        l_after  := to_number(json_value(:P2_CLASSIC_RPT_SORT_INFO,  ''$.sequence.after[''||i||'']''));',
'',
'        select priority into l_bef_sq',
'        from flw_type',
'        where id = l_before;',
'',
'        l_sqs := l_sqs || '':'' || l_bef_sq;',
'        l_ids := l_ids || '':'' || l_after;',
'',
'    end loop;',
'',
'    l_vc_arr_a_sq := apex_string.split(trim('':'' from l_sqs),'':'');',
'',
'    for j in (',
'        select rownum as rnum, t.column_value as spc_id',
'        from apex_string.split(trim('':'' from l_ids), '':'') t',
'    )',
'    loop',
'        update flw_type',
'        set    priority = l_vc_arr_a_sq(j.rnum)',
'        where  id = j.spc_id;',
'    end loop;',
'end;'))
