'    l_count         number;',
'    l_dummy         number := 0;',
'begin',
'    select json_value(:P6_CLASSIC_RPT_SORT_INFO, ''$.sequence.before.size()'') as result ',
'    into l_count',
'    from dual;',
'    ',
'    for i in 0 .. l_count-1',
'    loop',
'        ',
'        l_before := to_number(json_value(:P6_CLASSIC_RPT_SORT_INFO, ''$.sequence.before[''||i||'']''));',
'        l_after  := to_number(json_value(:P6_CLASSIC_RPT_SORT_INFO,  ''$.sequence.after[''||i||'']''));',
'',
'        select display_seq into l_bef_sq',
'        from flw_type_step_option',
'        where id = l_before;',
'',
'        l_sqs := l_sqs || '':'' || l_bef_sq;',
'        l_ids := l_ids || '':'' || l_after;',
'',
'    end loop;',
'',
'    l_vc_arr_a_sq := apex_string.split(trim('':'' from l_sqs),'':'');',
'    ',
'    -- update sequence for each option',
'    for j in (',
'        select rownum as rnum, t.column_value as spc_id',
'        from apex_string.split(trim('':'' from l_ids), '':'') t',
'    )',
'    loop',
'        update flw_type_step_option',
'        set    display_seq = l_vc_arr_a_sq(j.rnum)',
'        where  id = j.spc_id;',
'    end loop;',
'',
'    -- check that sequence is unique within this step',
'    begin',
'        select 1 into l_dummy',
'        from flw_type_step_option',
'        where flw_type_step_id = :P6_ID',
'        group by display_seq ',
'        having count(1) > 1;',
'',
'        raise_application_error(-20001, ''Error while changing sequence'');',
'        ',
'    exception when no_data_found then',
'        null;',
'    end;',
'',
'end;'))
,p_attribute_02=>'P6_CLASSIC_RPT_SORT_INFO'
,p_attribute_05=>'#SQLERRM#'
,p_attribute_15=>'escape-message'
,p_wait_for_result=>'Y'
);
