,p_process_sequence=>20
,p_process_point=>'AFTER_SUBMIT'
,p_region_id=>wwv_flow_imp.id(105562202606431428006)
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'LOV''s - Save Interactive Grid Data_1'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_lang_id  spc_lang.id%type;',
'begin',
'     case :APEX$ROW_STATUS',
'     when ''C'' then ',
'         -- insert new record into spc_lov',
'         insert into spc_lov ',
'            ( list_group',
'            , list_code',
'            , value_code',
'            , active_ind',
'            , system_ind',
'            , created',
'            , created_by',
'            ) values (',
'              :LIST_GROUP',
'            , :LIST_CODE',
'            , :VALUE_CODE',
'            , :ACTIVE_IND',
'            , :SYSTEM_IND',
'            , sysdate',
'            , :APP_USER)',
'         returning ID into :ID;',
'',
'         -- get the language id for default language',
'            select id ',
'            into l_lang_id ',
'            from spc_lang ',
'            where default_lang_ind = 1;',
'',
'         -- insert new record into spc_lov_lang',
'         if :DESCRIPTION is not null then ',
'             insert into spc_lov_lang ',
'                ( spc_lang_id',
'                , spc_lov_id',
'                , description',
'                ) values (',
'                  l_lang_id --:SPC_LANG_ID',
'                , :ID',
'                , :DESCRIPTION);   ',
'          end if;  ',
'     when ''U'' then',
'        -- update spc_lov_lang',
'         update spc_lov',
'            set list_group = :LIST_GROUP',
'                ,list_code = :LIST_CODE',
'                ,value_code = :VALUE_CODE',
'                ,active_ind = :ACTIVE_IND',
'                ,system_ind = :SYSTEM_IND',
'          where id  = :ID;',
'        ',
'        -- get the language id for default language',
'            select id ',
'            into l_lang_id ',
'            from spc_lang ',
'            where default_lang_ind = 1;',
'',
'        -- update description',
'         update spc_lov_lang',
'         set description = :DESCRIPTION',
'         where spc_lang_id = l_lang_id',
'         and spc_lov_id = :ID;',
'',
'     when ''D'' then',
'        if :SYSTEM_IND = 1 then',
'            raise_application_error(-20000, ''Cannot delete a system list value.'');',
'        else ',
'            -- delete from spc_lov_lang',
'            delete spc_lov_lang',
'            where spc_lov_id = :ID;',
'',
'            -- delete from spc_lov    ',
'            delete spc_lov',
'            where id = :ID;',
'        end if;',
'     end case;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
);
